// Paleta colores
$primario: #4c8d26;
$secundario: #d5fb00;
$complementario: #882380;
$dark: #223c20;
$light: #f8fafc;

// Breakpoints
$small: 480px;
$medium: 768px;
$large: 1024px;
$extra-large: 1600px;

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  text-decoration: none;
  list-style: none;
}

html {
  font-size: 62.5%;
  font-family: "Poppins", sans-serif;
}

body {
  line-height: 1.5;
}

img {
  width: 100%;
}

.container {
  width: 100%;
  padding: 0 2rem;
}

.btn {
  background-color: $light;
  padding: 1rem 2rem;
  font-family: "Poppins", sans-serif;
  border: none;
  border-radius: 1rem;
  border-bottom: 0.4rem solid $complementario;
  box-shadow: 0.1rem 0.1rem 0.2rem $dark;
}

