@use "./../customs/globals" as *;

.header {
  position: relative;

  &__home {
    background-image: url("../img/hero_landscape.jpg");
    background-position: center center;
    background-size: cover;

    height: 100vh;
    width: 100vw;

    display: flex;
    flex-direction: column;
    justify-content: space-around;
    align-items: center;
  }

  &__title {
    height: 100vh;
    margin: 20vh 0;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
  }

  &__logo {
    color: $light;
    font-size: 4rem;

    &--text {
      color: $light;
    }
  }

  &__heading {
    color: $secundario;
    font-size: 4rem;
    text-shadow: 0.2rem 0.2rem 0.2rem lighten($color: $secundario, $amount: 25%);

    @media (min-width: $medium) {
      font-size: 4.8rem;
    }
    @media (min-width: $large) {
      font-size: 6rem;
      width: 75%;
    }
  }
  h2 {
    color: $complementario;
    padding: 3rem 8rem;
    font-size: 3.8rem;
    text-shadow: 0.2rem 0.2rem 0.2rem lighten($color: $secundario, $amount: 25%);

    @media (min-width: $medium) {
      font-size: 4.4rem;
    }
    @media (min-width: $large) {
      font-size: 5.2rem;
    }
  }
}

