@use "./../customs/mixins" as *;

.header {
  position: relative;

  &__home {
    background-image: url("../img/hero_landscape.jpg");
    background-position: center center;
    background-size: cover;

    height: 100vh;
    width: 100vw;

    display: flex;
    flex-direction: column;
    justify-content: space-around;
