  color: $light;
  position: relative;

  background-image: linear-gradient(
      to bottom,
      transparentize($light, 0.7) 0%,
      $dark 100%
    ),
    url(../img/hero_footer.jpg);
  background-position: center center;
  background-size: cover;
  background-attachment: fixed;

  display: flex;
  flex-direction: column;
  align-items: center;

  &__top {
    height: 100%;
    padding: 25vh 0;
