    flex-direction: column;
    justify-content: center;
    align-items: center;
  }

  &__heading {
    text-align: center;
    font-size: 3.6rem;

    @media (min-width: $medium) {
      font-size: 4rem;
    }
    @media (min-width: $large) {
      font-size: 4.8rem;
    }
    @media (min-width: $large) {
      font-size: 6rem;
    }
  }

  &__bottom {
    width: 100%;
    display: grid;
    place-items: start;
    padding-bottom: 2rem;
    font-size: 1.4rem;
    grid-template-columns: repeat(2, 1fr);
    @include borderSingle($light);
  }
}

.footer__form {
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: relative;

  & input:not([type="submit"]) {
    font-family: "Poppins", sans-serif;
    background-color: transparent;
    width: 100%;
    margin: 2rem 0;
    padding: 1rem 0;
    border: none;
    outline: none;
    border-bottom: 0.4rem solid $complementario;
    border-radius: 1rem;

    &::placeholder,
    &:focus {
      text-align: center;
      font-family: "Poppins, " sans-serif;
      color: darken($color: $light, $amount: 5%);
      color: 1.2rem;
      font-size: 1.6rem;
    }
  }
}

